import React, {useState,useEffect} from 'react';
import {View, Text, Dimensions, StyleSheet, Image, FlatList, KeyboardAvoidingView, ScrollView} from 'react-native';
import {db, auth} from '../../firebase2'
import {Button, Card} from "react-native-elements";
import {useNavigation} from "@react-navigation/core";
import image from '../assets/articulo.jpg'
const Articulos = ({ info }) => {
    const [articulos, setArticulos] = useState([]);
    const navigation = useNavigation()
    const [blogsList, setBlogsList] = useState([]);
    useEffect(()=>{
        db.collection('Articulos').onSnapshot(querySnapshot=>{
            const lista = []
            querySnapshot.docs.forEach(doc=>{
                const {name, description, tags, nickname} = doc.data()
                lista.push({
                    id:doc.id,name,description,tags, nickname
                })

            })
            setBlogsList([...lista])
        })

    },[])
    React.useEffect(()=>{
        db.collection('correosPsicologos').onSnapshot(querySnapshot=>{
            const docs = [];
            querySnapshot.forEach(doc=>{
                docs.push({
                    id: doc.id,
                    ...doc.data(),
                })
            })
            setArticulos([...docs]);
            console.log(docs)
        })

    },[])
    function renderItem ({item}) {
        return (
            <View style={{marginTop:10,backgroundColor: '#e1e1e1'}}>
                <ScrollView showsVerticalScrollIndicator={false}>
                    <View style={{backgroundColor: '#e1e1e1', marginTop:0, marginBottom:0}}>
                        <Card containerStyle={{marginLeft:0, marginRight:0, marginTop:0, marginBottom:0, height:"100%",width:"100%", backgroundColor:'#f6f6f6'}}>
                            <Card.Title onPress={()=>{navigation.navigate('ArticuloScreen',{userId: item.id})}}>{item.name}</Card.Title>
                            <Card.Divider />
                            <View
                                style={{
                                    position: "relative",
                                    alignItems: "center"
                                }}
                            >
                                <Image
                                    onPress={()=>{navigation.navigate('ArticuloScreen',{userId: item.id})}}
                                    source={image}
                                    style={{ width: 450, height: 200 }}
                                    resizeMode="contain"
                                />
                                <Text style={{marginTop:10}}>{item.nickname}</Text>
                                <Text style={{marginTop:10}}>{item.tags}</Text>
                            </View>
                        </Card>

                    </View>

                </ScrollView>

            </View>
        );
    }
    return(
        <KeyboardAvoidingView
            style={styles.container}
            behavior="padding"
        >

            <View>
                <FlatList
                    showsVerticalScrollIndicator={false}
                    showsHorizontalScrollIndicator={false}
                    data={blogsList}
                    renderItem={renderItem}
                    keyExtractor={item => item.id}
                />
                <View>
                    <View style={styles.fixToText}>
                        {function(){
                            if(articulos.length>0){
                                console.log(articulos[0].correos)
                                if(articulos[0].correos.includes(auth.currentUser.email)){
                                    return(
                                        <Button
                                            title="Escribir artí­culo"
                                            buttonStyle={{
                                                backgroundColor: '#00ADC7',

                                            }}
                                            onPress={() => navigation.navigate('EscribirArticulo')}
                                        />
                                    )
                                }
                            }

                        }()}


                    </View>
                </View>

            </View>

        </KeyboardAvoidingView>
    )


}


const deviceWidth = Math.round(Dimensions.get('window').width);
const offset = 40;
const radius = 20;
const styles = StyleSheet.create({
    container: {
        width: deviceWidth,
        alignItems: 'center',
        marginTop: 25,
    },
    button: {
        margin: 2,
    },
    fixToText: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    indicator: {
        justifyContent: 'center',
        alignItems: 'center',
    },
    cardContainer: {
        width: deviceWidth - offset,
        backgroundColor: '#fff',
        height: 230,
        borderRadius: radius,
        marginBottom: 20,

        shadowColor: '#000',
        shadowOffset: {
            width: 5,
            height: 5,
        },
        shadowOpacity: 0.75,
        shadowRadius: 5,
        elevation: 10,
    },
    imageStyle: {
        height: 130,
        width: deviceWidth - offset,
        borderTopLeftRadius: radius,
        borderTopRightRadius: radius,
        opacity: 0.9,
        alignContent: 'center',
        alignSelf: 'center',
    },
    titleStyle: {
        fontSize: 20,
        fontWeight: '800',
    },
    categoryStyle: {
        fontWeight: '200',
    },
    infoStyle: {
        marginHorizontal: 10,
        marginVertical: 5,
    },
    iconLabelStyle: {
        flexDirection: 'row',
        marginTop: 10,
    },
});

export default Articulos;
